import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { APIGetPaged, APIResponse } from '@app/models';
import { Card } from '@app/pages/cards/card/card';

export class GetCards extends APIGetPaged {
	constructor(init?: Partial<GetCards>) {
		super();
		Object.assign(this, init);
	}
}

export class GetCardsFiltered extends APIGetPaged {
	constructor(init?: Partial<GetCards>) {
		super();
		Object.assign(this, init);
	}
}

export interface CardResults {
	total_value?: number;
	total_results: number;
	total_pages: number;
	cards?: Card[];
}

@Injectable({ providedIn: 'root' })
export class CardsService {
	constructor(private http: HttpClient) {}

	// Get cards
	private getCardsSubject = new BehaviorSubject<CardResults>(null);
	getCardsObservable() {
		this.getCardsSubject = new BehaviorSubject<CardResults>(null);
		return this.getCardsSubject.asObservable();
	}
	getCards(params: GetCards) {
		var url = params.buildUrl('cards');
		this.http.get<APIResponse>(url).subscribe((res) => {
			let cards = [];
			res.data.forEach((card) => {
				cards.push(new Card(card));
			});
			this.getCardsSubject.next({
				cards: cards,
				total_pages: res.meta.last_page,
				total_results: res.meta.total,
			});
		});
	}

	// Get cards filtered
	private getCardsFilteredSubject = new BehaviorSubject<CardResults>(null);
	getCardsFilteredObservable() {
		this.getCardsFilteredSubject = new BehaviorSubject<CardResults>(null);
		return this.getCardsFilteredSubject.asObservable();
	}
	getCardsFiltered(params: GetCardsFiltered) {
		var url = params.buildUrl('cards/filter');
		this.http.get<APIResponse>(url).subscribe((res) => {
			let cards = [];
			res.data.forEach((card) => {
				cards.push(new Card(card));
			});
			this.getCardsFilteredSubject.next({
				cards: cards,
				total_pages: res.meta.last_page,
				total_results: res.meta.total,
			});
		});
	}
}

import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { environment } from '@app/../environments/environment';
import { APIResponse } from '@app/models';
import { Expansion, Series } from '@app/pages/expansions/expansion/expansion';
import { CacheGlobal } from './cache/globalCache';

export interface GetExpansions {
	query: string;
	sort_by: string;
	sort_direction: string;
}

@Injectable({ providedIn: 'root' })
export class ExpansionsService {
	constructor(private http: HttpClient) {}

	// Get expansions
	private getExpansionsSubject = new BehaviorSubject<Series[]>(null);
	getExpansionsObservable() {
		this.getExpansionsSubject = new BehaviorSubject<Series[]>(null);
		return this.getExpansionsSubject.asObservable();
	}
	getExpansions(params: GetExpansions) {
		if (CacheGlobal.expansions) {
			this.getExpansionsSubject.next(
				this.handleExpansionsParams(params, CacheGlobal.expansions)
			);
		} else {
			this.http
				.get<APIResponse>(`${environment.api}expansions`)
				.subscribe((res) => {
					let series: Series[] = [];
					res.data.forEach((_series) => {
						series.push(new Series(_series));
					});
					CacheGlobal.expansions = series;
					this.getExpansionsSubject.next(
						this.handleExpansionsParams(params, series)
					);
				});
		}
	}
	handleExpansionsParams(
		params: GetExpansions,
		seriesCollection: Series[]
	): Series[] {
		// No data
		if (!seriesCollection.length) {
			return [];
		} else {
			let _seriesCollection: Series[] = [];

			// Query
			if (params.query && params.query.length) {
				// Filter expansions
				seriesCollection.forEach((series) => {
					let expansions = series.expansions.filter((expansion) => {
						return expansion.name
							.toLowerCase()
							.includes(params.query.toLowerCase());
					});
					if (expansions.length) {
						let _series = Object.assign({}, series);
						_series.expansions = expansions;
						_seriesCollection.push(_series);
					}
				});
			} else {
				_seriesCollection = seriesCollection;
			}

			// Sort
			function sortSeriesAsc(a: Series, b: Series) {
				if (a.id < b.id) {
					return -1;
				}
				if (a.id > b.id) {
					return 1;
				}
				return 0;
			}
			function sortSeriesDesc(a: Series, b: Series) {
				if (a.id > b.id) {
					return -1;
				}
				if (a.id < b.id) {
					return 1;
				}
				return 0;
			}
			function sortExpansionAsc(a: Expansion, b: Expansion) {
				if (new Date(a.release_date) < new Date(b.release_date)) {
					return -1;
				}
				if (new Date(a.release_date) > new Date(b.release_date)) {
					return 1;
				}
				return 0;
			}
			function sortExpansionDesc(a: Expansion, b: Expansion) {
				if (new Date(a.release_date) > new Date(b.release_date)) {
					return -1;
				}
				if (new Date(a.release_date) < new Date(b.release_date)) {
					return 1;
				}
				return 0;
			}
			switch (params.sort_direction) {
				case 'asc':
					_seriesCollection.sort(sortSeriesAsc);
					_seriesCollection.forEach((_series) => {
						_series.expansions.sort(sortExpansionAsc);
					});
					break;
				case 'desc':
					_seriesCollection.sort(sortSeriesDesc);
					_seriesCollection.forEach((_series) => {
						_series.expansions.sort(sortExpansionDesc);
					});
					break;
			}

			return _seriesCollection;
		}
	}
}

import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { environment } from '@app/../environments/environment';

import { APIResponse } from '@app/models/api';
import { ScannerList } from '@app/pages/scanner/scanner-lists/scanner-list/scanner-list';
import { Card } from '@app/pages/cards/card';
import { NotificationsService, Notification } from '@app/controls/notifications';
import { AlertType } from '@app/controls/alert/alert';

export enum ScanType {
	scan = "scan",
	multiple = "scan_multiple",
	snapshot = "snapshot"
}

export interface GetScanCardParams {
	image: string;
}

export interface GetScanCardsParams {
	image: string;
}

@Injectable({
    providedIn: 'root'
})

export class ScannerService {

    constructor(
		private http: HttpClient,
		private notificationService: NotificationsService) {}

	// TempId
	private tempId = 0;
	getTempId() {
		return this.tempId++;
	}

	// Scan single card
	private scanCardSubject = new BehaviorSubject<Card>(null);
    getScanCardObservable() {
		this.scanCardSubject = new BehaviorSubject<Card>(null);
		return this.scanCardSubject.asObservable();
	}
    getScanCard(params: GetScanCardParams) {
        this.http.post<APIResponse>(environment.api + "scanner/detect", params).subscribe(res => {
			if (res.success) {
				let card = new Card(res.data.card);
				if (card.id > 0) {
					this.scanCardSubject.next(card);
				}
			}
			else {
				this.scanCardSubject.next(null);
			}
		});
	}

	// Scan mutiple cards
	private scanCardsSubject = new BehaviorSubject<Card[]>([]);
	getScanCardsObservable() {
		this.scanCardsSubject = new BehaviorSubject<Card[]>([])
		return this.scanCardsSubject.asObservable();
	}
	getScanCards(params: GetScanCardsParams) {
		this.http.post<APIResponse>(environment.api + "scanner/multiple", params).subscribe(res => {
			if (res.success && res.data.length > 0) {
				let cards: Card[] = [];
				res.data.forEach(card => {
					let _card = new Card(card);
					if (_card.id > 0) {
						cards.push(_card);
					}
				});
				this.scanCardsSubject.next(cards);
			}
			else {
				this.scanCardsSubject.next([]);
			}
		});
	}
	
	// Scan cache
	private _scannerList: ScannerList = new ScannerList();
	get scannerList() {
		return this._scannerList;
	}
	set scannerList(scannerList) {
		this._scannerList = scannerList;
	}

	// Clear cache
	clearScans() {
		this._scannerList = new ScannerList();
	}

	// Scan cache
	private scansSubject = new BehaviorSubject<Card[]>([]);
	getScansObservable() {
		this.scansSubject = new BehaviorSubject<Card[]>([]);
		return this.scansSubject.asObservable();
	}
	getScans() {
		this.scansSubject.next(this._scannerList.cards);
	}

	changeVersion(cardOld: Card, cardNew: Card) {
		

		// Remove old card via tempId
		// this._scannerList.cards = this._scannerList.cards.filter(card => {
		// 	return card.tempId != cardOld.tempId;
		// });
		let updatedScannerListCards: Card[] = [];
		this._scannerList.cards.forEach(card => {
			if (card.tempId === cardOld.tempId) {
				updatedScannerListCards.push(cardNew);
			}
			else {
				updatedScannerListCards.push(card);
			}
		});


		this._scannerList.cards = updatedScannerListCards;

		// Add new card
		//cardNew.tempId = cardOld.tempId++;
		//this._scannerList.cards.push(cardNew);

		// Update subscriptions
		this.scansSubject.next(this._scannerList.cards);
	}

	removeCard(card: Card) {
		this._scannerList.cards = this._scannerList.cards.filter(_card => {
			return _card.tempId != card.tempId;
		});

		// Update subscriptions
		this.scansSubject.next(this._scannerList.cards);

		this.notificationService.addNotifications([
			new Notification({
				alertType: AlertType.success,
				message: "Removed " + card.name + " from results"
			})
		]);
	}

}

