import { Card } from "@app/pages/cards";
import { BehaviorSubject } from "rxjs";
import { environment } from "src/environments/environment";
import { Pokemon, PokemonVariant } from "../pages/pokemons/pokemon/pokemon";
import { CacheGlobal } from "./cache/globalCache";

// Get pokemon interfaces
export class GetPokemonVariantCards extends APIGetPaged {
	slug: string;
	constructor(init?:Partial<GetPokemonVariantCards>) {
		super();
		Object.assign(this, init);
	}
}
export interface ResPokemonVariantCards {
	total_results: number;
	total_pages: number;
	cards: Card[];
}

@Injectable({
    providedIn: 'root'
})

export class PokemonService {

	constructor(private http: HttpClient) {}

	// Get pokemon
	private getPokemonSubject = new BehaviorSubject<Pokemon>(null);
	getPokemonObservable() {
		this.getPokemonSubject = new BehaviorSubject<Pokemon>(null);
		return this.getPokemonSubject.asObservable();
	}
	getPokemon(slug: string) {
		if (CacheGlobal.pokemon[slug]) {
			this.getPokemonSubject.next(CacheGlobal.pokemon[slug]);
		}
		else {
			this.http.get<APIResponse>(`${environment.api}pokemon/${slug}`).subscribe(res => {
				this.getPokemonSubject.next(new Pokemon(res.data));
			});
		}
	}

	// Get pokemon variant
	private getPokemonVariantSubject = new BehaviorSubject<PokemonVariant>(null);
	getPokemonVariantObservable() {
		this.getPokemonVariantSubject = new BehaviorSubject<PokemonVariant>(null);
		return this.getPokemonVariantSubject.asObservable();
	}
	getPokemonVariant(slug: string) {
		if (CacheGlobal.pokemonVariant[slug]) {
			this.getPokemonVariantSubject.next(CacheGlobal.pokemonVariant[slug]);
		}
		else {
			this.http.get<APIResponse>(`${environment.api}pokemon-variants/${slug}`).subscribe(res => {
				this.getPokemonVariantSubject.next(new PokemonVariant(res.data));
			});
		}
	}

	// Get pokemon variant cards
	private getPokemonVariantCardsSubject = new BehaviorSubject<ResPokemonVariantCards>(null);
	getPokemonVariantCardsObservable() {
		this.getPokemonVariantCardsSubject = new BehaviorSubject<ResPokemonVariantCards>(null);
		return this.getPokemonVariantCardsSubject.asObservable();
	}
	getPokemonVariantCards(params: GetPokemonVariantCards) {
		var url = params.buildUrl(`pokemon-variants/${params.slug}/cards`);
		this.http.get<APIResponse>(url).subscribe(res => {
			let cards = [];
			res.data.forEach(card => {
				cards.push(new Card(card));
			});
			this.getPokemonVariantCardsSubject.next({
				total_pages: res.meta.last_page,
				total_results: res.meta.total,
				cards: cards
			});
		});
	}

}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { APIGetPaged, APIResponse } from '@app/models';
import { BehaviorSubject } from 'rxjs';
import { environment } from 'src/environments/environment';
import { Pokemon, PokemonVariant } from '../pages/pokemons/pokemon/pokemon';

export class GetPokemons extends APIGetPaged {
	constructor(init?: Partial<GetPokemons>) {
		super();
		Object.assign(this, init);
	}
}

export interface ResPokemons {
	total_results: number;
	total_pages: number;
	pokemons?: Pokemon[];
}

export class GetPokemonVariants extends APIGetPaged {
	type: string;
	constructor(init?: Partial<GetPokemonVariants>) {
		super();
		Object.assign(this, init);
	}
}

export interface ResPokemonVariants {
	total_results: number;
	total_pages: number;
	pokemon_variants?: PokemonVariant[];
}

@Injectable({
	providedIn: 'root',
})
export class PokemonsService {
	constructor(private http: HttpClient) {}

	// Get all pokemons
	private getPokemonsSubject = new BehaviorSubject<ResPokemons>(null);
	getPokemonsObservable() {
		this.getPokemonsSubject = new BehaviorSubject<ResPokemons>(null);
		return this.getPokemonsSubject.asObservable();
	}
	getPokemons(params: GetPokemons) {
		var url = params.buildUrl('pokemon');
		this.http.get<APIResponse>(url).subscribe((res) => {
			let pokemons: Pokemon[] = [];
			res.data.forEach((pokemon) => {
				pokemons.push(new Pokemon(pokemon));
			});
			this.getPokemonsSubject.next({
				total_pages: res.meta.last_page,
				total_results: res.meta.total,
				pokemons: pokemons,
			});
		});
	}

	// Get all pokemons
	private getPokemonVariantsSubject = new BehaviorSubject<ResPokemonVariants>(
		null
	);
	getPokemonVariantsObservable() {
		this.getPokemonVariantsSubject =
			new BehaviorSubject<ResPokemonVariants>(null);
		return this.getPokemonVariantsSubject.asObservable();
	}
	getPokemonVariants(params: GetPokemonVariants) {
		var url = params.buildUrl('pokemon-variants');
		this.http.get<APIResponse>(url).subscribe((res) => {
			let pokemon_variants: PokemonVariant[] = [];
			res.data.forEach((pokemon_variant) => {
				pokemon_variants.push(new PokemonVariant(pokemon_variant));
			});
			this.getPokemonVariantsSubject.next({
				total_pages: res.meta.last_page,
				total_results: res.meta.total,
				pokemon_variants: pokemon_variants,
			});
		});
	}
}

