import { Pokemon } from '@app/pages/pokemons/pokemon/pokemon';

export const DEFAULT_IMAGE = '/assets/back.jpg';

export class Weakness {
	type: string;
	value: string;
}

export class Attack {
	cost: string[] = [];
	name: string;
	text: string;
	damage: number;
	convertedEnergyCost: number;
}

export class Card {
	id: number;
	name: string;
	slug: string;
	pokemon: Pokemon;
	expansion: Expansion;
	number: string;
	rarity: string;
	image: string;
	image_high_res?: string;
	super_type: string;
	sub_type?: string;
	hp?: number;
	retreat_cost: string[] = [];
	weaknesses: Weakness[] = [];
	attacks: Attack[] = [];
	artist: string;
	is_shiny: boolean;
	is_promo: boolean;
	is_full_art: boolean;
	is_gold: boolean;
	has_first_edition: boolean;
	has_shadowless: boolean;
	has_reverse_holo: boolean;
	types: string[] = [];
	last_prices: CardLastPrices;
	user_cards: CardCollectionItem[] = [];

	route: string;
	tempId: number;

	getCardNumber(): string {
		// IS number
		if (!isNaN(+this.number)) {
			let x = `${this.number}`;
			let y = `${this.expansion.total_cards}`;
			let pad = '000';
			let xFormatted = pad.substring(0, pad.length - x.length) + x;
			let yFormatted = pad.substring(0, pad.length - y.length) + y;
			return `${xFormatted}/${yFormatted}`;
		}
		// NOT number (promo)
		else {
			return this.number;
		}
	}

	constructor(init?: Partial<Card>) {
		Object.assign(this, init);

		// Route
		this.route = '/cards/' + this.slug;

		// Initalize expansion
		if (init.expansion) {
			this.expansion = new Expansion(init.expansion);
		}

		// Initalize pokemon
		if (init.pokemon) {
			this.pokemon = new Pokemon(init.pokemon);
		}
	}
}

export class CardLastPrices {
	card_id: number;
	condition: string;
