import { AddUserCard, UserCardsService } from './card-collection.service';
import { Component, Input, OnInit } from '@angular/core';
import { GUID } from '@app/helpers';
import { Condition, Icons, Printings } from '@app/models';
import { Button } from '../../controls/button';
import { Checkbox } from '../../controls/checkbox';
import { Empty } from '../../controls/empty';
import { CardCollectionItem } from './card-collection-item';

@Component({
	selector: 'mb-card-collection',
	templateUrl: './card-collection.component.html',
	styleUrls: ['./card-collection.component.scss'],
})
export class CardCollectionComponent implements OnInit {
	@Input() card_id: number;
	@Input() userCards: CardCollectionItem[] = [];
	buttonAdd: Button;
	checkboxWishList: Checkbox;
	buttonViewAll: Button;
	empty: Empty;

	constructor(private cardCollectionService: UserCardsService) {}

	ngOnInit(): void {
		this.setupControls();
		this.setupSubscriptions();
	}

	setupControls() {
		this.empty = new Empty({
			text: 'This card is not in your collection',
			icon: Icons.box,
			button: new Button({
				text: 'Add to Collection',
				icon: Icons.plus,
				click: () => {
					this.addItem();
				},
			}),
		});

		// Button add
		this.buttonAdd = new Button({
			text: 'Add to Collection',
			icon: Icons.plus,
			classes: 'secondary',
			click: () => {
				this.addItem();
			},
		});

		// Button view all
		this.buttonViewAll = new Button({
			text: 'View Collection',
			icon: Icons.externalLink,
		});

		// Checkbox wish list
		this.checkboxWishList = new Checkbox({
			text: 'Wishlist',
		});
	}

	setupSubscriptions() {
		this.cardCollectionService
			.addUserCardObservable()
			.subscribe((addedCard) => {
				if (addedCard) {
					this.userCards.push(addedCard);
				}
			});
	}

	addItem(
		userCard: CardCollectionItem = new CardCollectionItem({
			card_id: this.card_id,
		})
	) {
		this.cardCollectionService.addUserCard(userCard);
	}

	deleteItem(userCard: CardCollectionItem) {
		this.cardCollectionService
			.removeUserCard(userCard.id)
			.subscribe((res) => {
				if (res.success)
					this.userCards = this.userCards.filter(
						(_userCard) => _userCard.id !== userCard.id
					);
			});
	}

	updateItem(userCard: CardCollectionItem) {
		this.cardCollectionService.updateUserCard(userCard).subscribe((res) => {
			if (res.success)
				this.userCards = this.userCards.map((_userCard) =>
					_userCard.id === userCard.id ? userCard : _userCard
				);
		});
	}
}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import {
	Condition,
	ConditionGraded,
	GradingCompany,
	Icons,
	Printings,
} from '@app/models';
import {
	Dialog,
	FormComponent,
	MBFormControl,
	MBFormGroup,
	Textarea,
} from '@app/controls';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Select, SelectOption, SelectOptionGroup } from '@app/controls/select';

import { Button } from '@app/controls/button';
import { CardCollectionItem } from './card-collection-item';
import { DialogService } from './../../../controls/dialog/dialog.service';
import { MBForm } from './../../../controls/form/form';
import { Textbox } from '@app/controls/textbox';

@Component({
	selector: 'mb-card-collection-item',
	templateUrl: './card-collection-item.component.html',
	styleUrls: ['./card-collection-item.component.scss'],
})
export class CardCollectionItemComponent implements OnInit {
	@Input() item: CardCollectionItem;
	@Output() deleted: EventEmitter<boolean> = new EventEmitter();
	@Output() updated: EventEmitter<CardCollectionItem> = new EventEmitter();
	selectCondition: Select;
	selectGradingCompany: Select;
	selectPrinting: Select;
	selectBinder: Select;
	buttonNotes: Button;
	buttonAdd: Button;
	buttonRemove: Button;
	formNotes: FormGroup;

	constructor(
		private dialogService: DialogService,
		private formBuilder: FormBuilder
	) {}

	ngOnInit(): void {
		this.buildControls();
	}

	buildControls() {
		// Notes
		this.formNotes = this.formBuilder.group({
			notesControl: [''],
		});

		// Condition
		this.selectCondition = new Select({
			value: this.item.condition,
			classes: 'square-right',
			optionGroups: [
				new SelectOptionGroup({
					label: 'Condition',
				}),
				new SelectOptionGroup({
					label: 'Condition Graded',
				}),
			],
			change: (value) => {
				this.updated.emit(
					new CardCollectionItem({
						...this.item,
						condition: Condition[value],
					})
				);
			},
		});
		for (let condition in Condition) {
			this.selectCondition.optionGroups[0].options.push(
				new SelectOption({
					text: Condition[condition],
					value: Condition[condition],
					selected: Condition[condition] === this.item.condition,
				})
			);
		}
		for (let conditionGraded in ConditionGraded) {
			this.selectCondition.optionGroups[1].options.push(
				new SelectOption({
					text: ConditionGraded[conditionGraded],
					value: ConditionGraded[conditionGraded],
					selected:
						ConditionGraded[conditionGraded] ===
						this.item.condition,
				})
			);
		}
		this.selectCondition.value = this.item.condition;

		// Grading company
		this.selectGradingCompany = new Select({
			classes: 'square',
			optionGroups: [
				new SelectOptionGroup({
					label: 'Graded By',
				}),
			],
		});
		for (let gradingCompany in GradingCompany) {
			this.selectGradingCompany.optionGroups[0].options.push(
				new SelectOption({
					text: GradingCompany[gradingCompany],
					value: GradingCompany[gradingCompany],
				})
			);
		}

		// Printing
		this.selectPrinting = new Select({
			classes: 'square',
			optionGroups: [
				new SelectOptionGroup({
					label: 'Printing',
				}),
			],
			change: (value) => {
				this.updated.emit(
					new CardCollectionItem({
						...this.item,
						printing: Printings[value],
					})
				);
			},
		});
		for (let printVersion in Printings) {
			this.selectPrinting.optionGroups[0].options.push(
				new SelectOption({
					text: Printings[printVersion],
					value: Printings[printVersion],
				})
			);
		}

		// Notes
		this.buttonNotes = new Button({
			text: 'Notes',
			classes: 'square secondary',
			width: '100%',
			click: () => {
				this.dialogService.setDialog(
					new Dialog({
						title: 'Notes',
						form: new MBForm({
							formGroup: this.formNotes,
							groups: [
								new MBFormGroup({
									controls: [
										new MBFormControl({
											formControl:
												this.formNotes.controls
													.notesControl,
											control: new Textarea({}),
										}),
									],
								}),
							],
						}),
					})
				);
			},
		});

		// Binder
		this.selectBinder = new Select({
			classes: 'square',
			multiple: true,
			options: [
				new SelectOption({
					text: 'Binder 1',
					value: 'binder1',
					selected: true,
				}),
			],
		});

		// Button remove
		this.buttonRemove = new Button({
			icon: Icons.trash,
			classes: 'secondary square-left',
			click: () => {
				if (confirm('Are you sure you want to delete this item?')) {
					this.deleted.emit(true);
				}
			},
		});
	}
}

