import { Component, OnInit } from '@angular/core';

@Component({
	selector: 'collection-card-group-cards',
	templateUrl: 'collection-card-group-cards.component.html',
	styleUrls: ['./collection-card-group-cards.component.scss'],
})
export class CollectionCardGroupCardsComponent implements OnInit {
	constructor() {}
	ngOnInit() {}
}

import { Icons } from '@app/models';
import { Router } from '@angular/router';
import { AuthenticationService } from '@app/services/auth.service';
import {
	Textbox,
	Button,
	Textarea,
	Toggle,
	Select,
	SelectOption,
	ButtonType,
} from '@app/controls';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Component, OnInit } from '@angular/core';

@Component({
	selector: 'collection-card-group',
	templateUrl: 'collection-card-group.component.html',
	styleUrls: ['./collection-card-group.component.scss'],
})
export class CollectionCardGroupComponent implements OnInit {
	form: FormGroup;
	textboxName: Textbox;
	selectType: Select;
	selectIcon: Select;
	textareaDescription: Textarea;
	togglePublic: Toggle;
	buttonSave: Button;
	buttonCancel: Button;
	constructor(
		private formBuilder: FormBuilder,
		private authenticationService: AuthenticationService,
		private router: Router
	) {
		if (!this.authenticationService.currentUserValue) {
			this.router.navigateByUrl('/');
		}
	}

	ngOnInit() {
		this.form = this.formBuilder.group({
			nameControl: ['', Validators.required],
			selectType: ['', Validators.required],
			selectIcon: ['', Validators.required],
			descriptionControl: [''],
			publicControl: [''],
		});
		this.textboxName = new Textbox({
			label: 'Name',
		});
		this.selectType = new Select({
			label: 'Type',
			advancedSelect: true,
			multiple: true,
			placeholder: 'Select icon...',
			options: [
				new SelectOption({
					text: 'Binder',
					icon: Icons.binder,
				}),
				new SelectOption({
					text: 'Deck',
					icon: Icons.deck,
				}),
				new SelectOption({
