	background: rgba(black, .8);
	border-radius: $border-radius-small;
	color: white;
	padding: $spacing + 1px ($spacing * 2);
	padding-right: 40px;
	box-shadow: $shadow;
	transition: $transition, ease .2s height, ease .2s padding, ease .2s margin;
	margin-bottom: $spacing;
	position: relative;
	//height: 40px;
	max-width: 100%;
	width: 400px;
	opacity: 1;
	.notification-message {
		text-overflow: ellipsis;
		overflow: hidden;
		display: block;
		max-width: 100%;
	}
	.close {
		cursor: pointer;
		position: absolute;
		top: 0;
		right: 0;
		bottom: 0;
		background: rgba(black, .2);
		border-top-right-radius: $border-radius-small;
		border-bottom-right-radius: $border-radius-small;
		padding: $spacing;
		transition: $transition;
		display: flex;
		flex-direction: column;
		justify-content: center;
		fa-icon {
			margin: 0;
			width: 16px;
			height: 16px;
		}
		&:hover {
			background: rgba(black, .4);
			box-shadow: $shadow;
		}
	}
	&.hidden {
		height: 0;
		padding-top: 0;
		padding-bottom: 0;
		opacity: 0;
		overflow: hidden;
		margin: 0;
		pointer-events: none;
		&:hover, &:active {
			box-shadow: none;
		}
	}
	fa-icon {
		width: 24px;
		height: 24px;
		margin-right: $spacing;
	}
	&:hover, &:active {
		box-shadow: $shadow, $shadow-focus;
	}
}

