	templateUrl: './profile.component.html',
})
export class ProfileComponent implements OnInit {
	menu: Menu;

	constructor(private authenticationService: AuthenticationService) {}

	ngOnInit(): void {
		this.menu = new Menu({
			classes: 'round',
			clearActiveClickOutside: true,
			horizontal: true,
			items: [
				new MenuItem({
					text: this.authenticationService.currentUserValue.username,
					icon: Icons.user,
					menu: new Menu({
						classes: 'anchor-right',
						items: [
							new MenuItem({
								text: 'Dashboard',
								icon: Icons.dashboard,
								route: '/collection/dashboard/',
								click: () => {
									this.menu.clearActive();
								},
							}),
							new MenuItem({
								text: 'Profile',
								icon: Icons.user,
								route: '/profile/edit',
								click: () => {
									this.menu.clearActive();
								},
							}),
							// ...( [new MenuItem({
							// 	text: "Admin",
							// 	icon: Icons.donate,
							// 	route: "patreon",
							// 	click: () => {
							// 		this.menu.clearActive();
							// 	}
							// })]),
							new MenuItem({
								text: 'Sign Out',
								icon: Icons.signOut,
								click: () => {
									this.authenticationService.logout();
								},
							}),
						],
					}),
				}),
			],
		});
	}
}

import { Component, OnInit, Input } from '@angular/core';
import { ProgressBar } from './progress-bar';

@Component({
	selector: 'progress-bar',
	templateUrl: 'progress-bar.component.html',
	styleUrls: ['./progress-bar.component.scss']
})

export class ProgressBarComponent implements OnInit {

	@Input() progressBar: ProgressBar;
	
	getWidth = () => {
		return `${Math.round(this.progressBar.value/this.progressBar.total * 100)}%`;
	}

	isComplete = () => {
		return this.progressBar.value >= this.progressBar.total;
	}

	constructor() { }

	ngOnInit() { }
}

