import { NotificationsService, Notification, AlertType } from '@app/controls';
import { Router, ActivatedRoute } from '@angular/router';
import { AuthenticationService } from '@app/services/auth.service';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { Textbox, Button, ButtonType } from '@app/controls';
import { Component, OnInit } from '@angular/core';

@Component({
	selector: 'mb-reset',
	templateUrl: './reset.component.html',
	styleUrls: ['./reset.component.scss'],
})
export class ResetComponent implements OnInit {
	form: FormGroup;
	textboxEmail: Textbox;
	textboxPassword: Textbox;
	textboxConfirmPassword: Textbox;
	buttonSubmit: Button;
	token: string;

	constructor(
		private authenticationService: AuthenticationService,
		private formBuilder: FormBuilder,
		private router: Router,
		private route: ActivatedRoute,
		private notificationService: NotificationsService
	) {
		if (this.authenticationService.currentUserValue) {
			this.router.navigateByUrl('/');
		}
	}

	ngOnInit(): void {
		// Get query params
		this.route.params.subscribe((params) => {
			this.token = params['token'];
		});

		// Build form
		this.form = this.formBuilder.group({
			emailControl: ['', Validators.required],
			passwordControl: ['', Validators.required],
			confirmPasswordControl: ['', Validators.required],
		});

		// Build form controls
		this.textboxEmail = new Textbox({
			label: 'Email',
		});
		this.textboxPassword = new Textbox({
			label: 'Password',
		});
		this.textboxConfirmPassword = new Textbox({
			label: 'Confirm Password',
		});
		this.buttonSubmit = new Button({
			text: 'Reset Password',
			type: ButtonType.submit,
		});
	}

	submit() {
		if (this.form.invalid) {
			return;
		}

		this.authenticationService
			.reset(
				this.token,
				this.form.controls.emailControl.value,
				this.form.controls.passwordControl.value,
				this.form.controls.confirmPasswordControl.value
			)
			.subscribe(() => {
				this.notificationService.addNotifications([
					new Notification({
						alertType: AlertType.success,
						message: 'Password Reset',
					}),
				]);
			});
	}
}

import { Component, OnInit, ViewEncapsulation, Version } from '@angular/core';
import { WebcamImage, WebcamInitError, WebcamUtil } from 'ngx-webcam';
import { Subject, Observable, Subscription } from 'rxjs';
import { ScannerService, ScanType } from '@app/services/scanner.service';
import { Router } from '@angular/router';
import { Card } from '@app/pages/cards/card';
import { Checkbox,  } from '@app/controls/checkbox';
import { Menu, MenuItem } from '@app/controls/menu';
import { NotificationsService, Notification } from '@app/controls/notifications';
import { AutoUnsubscribe } from "ngx-auto-unsubscribe";
import { CardsService } from '../cards';
import { Icons } from '@app/models/icons';
import { Title } from '@angular/platform-browser';
import { AlertType, Alert } from '@app/controls/alert/alert';
import { LoaderService } from '@app/controls';
import { AppSettings } from '@app/app';

@AutoUnsubscribe()
@Component({
	selector: 'mb-scanner',
	templateUrl: './scanner.component.html',
	encapsulation: ViewEncapsulation.None,
	styleUrls: ['./scanner.component.scss']
})

export class ScannerComponent implements OnInit {

	constructor(
		private titleService: Title,
		private scannerService: ScannerService,
		private cardsService: CardsService,
		private router: Router,
		private loaderService: LoaderService,
		private notificationService: NotificationsService) { }

	// Webcam options
	public lastImageSize: string;
	public showWebcam = true;
	public allowCameraSwitch = true;
	public deviceId: string;
	public multipleWebcamsAvailable = false;
	public videoOptions: MediaTrackConstraints = {};
	public errors: WebcamInitError[] = [];
	public webcamImage: WebcamImage = null;
	private trigger: Subject<void> = new Subject<void>();
	// switch to next / previous / specific webcam; true/false: forward/backwards, string: deviceId
	private nextWebcam: Subject<boolean | string> = new Subject<boolean | string>();

	// Service subscriptions
	scannerServiceSubscription: Subscription;
	scannerMultipleServiceSubscription: Subscription;

	// Options
	subscribedSingle: boolean = false; // use dto prevent no cards found when subscription initalizes
	subscribedMulti: boolean = false;
	searching: boolean = false;
	scanning: boolean = false;
	showOptions: boolean = false;
	timer: any;
	matches: Card[] = [];
	visibleMatches: Card[] = [];
	testError: string = "";
	prompt: boolean = false;
	lastMatchedCard: Card = null;
	totalNullResults: number = 0;
	soundEffect: HTMLAudioElement;
	scannerMode: ScanType = ScanType.snapshot; // Default mode
	scanned: boolean = false; // Prevent sound effect when adding matches from cache

	alert: Alert;

	checkboxBatchMode: Checkbox;
	menuScannerModes: Menu;
	menuItemModeSnapshot: MenuItem;
	menuItemModeScanner: MenuItem;
	menuItemModeMultiple: MenuItem;
	menuScannerOptions: Menu;
	showResult: boolean;
	scannerResultMenu: Menu;
	scannerResultMenuItem: MenuItem;
	get showActionScan(): boolean {
		return this.scannerMode == ScanType.scan;
	};
	get showActionSnapshot(): boolean {
		return this.scannerMode == ScanType.snapshot;
	};
	get showActionMultiple(): boolean {
		return this.scannerMode == ScanType.multiple;
	};

	setMode() {
		switch(this.scannerMode) {
			case ScanType.snapshot: {
				this.setModeSnapshot();
				break;
			}
			case ScanType.scan: {
				this.setModeScanner();
				break;
			}
			case ScanType.multiple: {
				this.setModeMultiple();
				break;
			}
		}
	}

	setModeSnapshot() {
		this.menuScannerModes.clearActive();
		this.scannerMode = ScanType.snapshot;
		this.menuItemModeSnapshot.active = true;
		this.setupService();
	}

	setModeScanner() {
		this.menuScannerModes.clearActive();
		this.scannerMode =  ScanType.scan;
		this.menuItemModeScanner.active = true;
		this.setupService();
	}

	setModeMultiple() {
		this.menuScannerModes.clearActive();
		this.scannerMode = ScanType.multiple;
		this.menuItemModeMultiple.active = true;
		this.setupService();
	}

	ngOnDestroy() { }
	ngOnInit() {
		WebcamUtil.getAvailableVideoInputs()
			.then((mediaDevices: MediaDeviceInfo[]) => {
				this.multipleWebcamsAvailable = mediaDevices && mediaDevices.length > 1;
				if (this.multipleWebcamsAvailable) {
					// Try to go to rear camera
					this.showNextWebcam(mediaDevices[1].deviceId);
				}
			});

		this.loadSoundEffect();

		this.titleService.setTitle(AppSettings.titlePrefix + 'Scanner');

		this.checkboxBatchMode = new Checkbox({
			checked: false,
			text: "Batch Mode"
		});

		// Alert
		this.alert = new Alert({
			message: `
				<ul class="unordered-list">
					<li>
						<b>Single mode:</b> this algorithm looks for a single card name.
					</li>
					<li>
						<b>Multiple mode:</b> this algorithm looks for multiple card names.
					</li>
					<li>
						For best results with either mode, make sure the card name is completely visible and in focus.
					</li>
					<li>
						Our algorithms primarily uses card name text detection, unfortunaly identifying specific sets is not supported.
					</li>
				</ul>
			`,
		});

		// Options
		let scannerOptionMenuItems: MenuItem[] = [];
		let switchCameraMenuItem = new MenuItem({
			text: "Switch Cameras",
			icon: Icons.sync,
			click: () => {
				this.showNextWebcam(true);
			}
		});

		// Only add switch camer option if multi cameras
		if (this.multipleWebcamsAvailable) {
			scannerOptionMenuItems.push(switchCameraMenuItem);
		}

		// Only show menu if have options available
		if (scannerOptionMenuItems.length) {
			this.menuScannerOptions = new Menu({
				classes: "round bg",
				horizontal: true,
				items: [
					new MenuItem({
						icon: Icons.settings,
						menu: new Menu({
							classes: "round bg",
							items: scannerOptionMenuItems
						})
					})
				]
			})
		}

		// Modes menu
		this.menuItemModeSnapshot = new MenuItem({
			icon: Icons.camera,
			classes: "box",
			text: "Single",
			click: () => {
				this.setModeSnapshot();
			}
		});
		this.menuItemModeScanner = new MenuItem({
			icon: Icons.play,
			classes: "box",
			text: "Scanner",
			click: () => {
				this.setModeScanner();
			}
		});
		this.menuItemModeMultiple = new MenuItem({
			icon: Icons.bringFront,
			classes: "box",
			text: "Multiple",
			click: () => {
				this.setModeMultiple();
			}
		});
		this.menuScannerModes = new Menu({
			clearActiveClickOutside: false,
			horizontal: true,
			classes: "bg square",
			items: [
				this.menuItemModeSnapshot,
				//this.menuItemModeScanner,
				this.menuItemModeMultiple
			]
		});
		
		if (this.scannerService.scannerList.cards.length) {
			this.addMatches(this.scannerService.scannerList.cards);
		}
		this.setMode();
	}
	
	setupService() {
		switch(this.scannerMode) {
			case ScanType.snapshot:
			case ScanType.scan: {
				this.setupScannerService();
				break;
			}
			case ScanType.multiple: {
				this.setupMultipleScannerService()
				break;
			}
		}
	}

	setupScannerService() {
		if (!this.scannerServiceSubscription) {
			this.scannerServiceSubscription = this.scannerService.getScanCardObservable().subscribe((card: Card) => {
				this.loaderService.clearItemLoading("scanning");
				this.searching = false;
				if (card) {
					if (this.scanning) {
						this.totalNullResults = 0;
					}
					if (this.scannerMode == ScanType.snapshot || !this.lastMatchedCard || this.lastMatchedCard.id != card.id) {
						this.addMatches([card]);
					}
				}
				else {
					if (this.scanning) {
						// Stop scanner if 10 null results
						// Prevents spamming server if user is not doing anything
						this.totalNullResults ++;
						if (this.totalNullResults > 10) {
							this.scannerStop();	
						}
					}
					else if (this.subscribedSingle) {
						this.notificationService.addNotifications([
							new Notification({
								alertType: AlertType.warning,
								message: "No card(s) found",
							})
						])
					}
				}
				this.subscribedSingle = true;
			});
		}
	}

	setupMultipleScannerService() {
		if (!this.scannerMultipleServiceSubscription) {
			this.scannerMultipleServiceSubscription = this.scannerService.getScanCardsObservable().subscribe((cards: Card[]) => {
				this.loaderService.clearItemLoading("scanning");
				this.searching = false;
				if (cards && cards.length > 0) {
					this.addMatches(cards);
				}
				else if (this.subscribedMulti) {
					this.notificationService.addNotifications([
						new Notification({
							alertType: AlertType.warning,
							message: "No card(s) found",
						})
					])
				}
				this.subscribedMulti = true;
			});
		}
	}

	addMatches(cards: Card[]) {
		if (this.scanned) {
			this.playSoundEffect();
		}
		cards.forEach((card) => {
			this.addMatch(card);
		});
	}

	addMatch(card: Card) {
		card.tempId = this.scannerService.getTempId();

		// Limit tray to 10
		if (this.visibleMatches.length >= 6) {
			this.visibleMatches.shift();
		}
		this.visibleMatches.push(card);
		this.matches.push(card);
		this.lastMatchedCard = card;

		// Update service cache
		this.scannerService.scannerList.cards = this.matches;
	}

	loadSoundEffect() {
		this.soundEffect = new Audio();
		this.soundEffect.src = "../../assets/audio/soundeffect.mp3";
		this.soundEffect.load();
	}

	playSoundEffect() {
		this.soundEffect.play();
	}

	triggerSnapshot(): void {
		this.trigger.next();
	}

	toggleWebcam(): void {
		this.showWebcam = !this.showWebcam;
	}

	handleInitError(error: WebcamInitError): void {
		this.errors.push(error);
	}

	showNextWebcam(directionOrDeviceId: boolean | string): void {
		// true => move forward through devices
		// false => move backwards through devices
		// string => move to device with given deviceId
		this.nextWebcam.next(directionOrDeviceId);
	}

	handleImage(webcamImage: WebcamImage): void {
		this.scanned = true;
		console.info('received webcam image', webcamImage);
		this.webcamImage = webcamImage;
		let bytes = (webcamImage.imageAsBase64.length * (3/4)) - 2;
		let kilobytes = bytes / 1000;
		this.lastImageSize = `${kilobytes}kb`;

		// Send request to server
		switch (this.scannerMode) {
			case ScanType.scan: {
				this.scannerService.getScanCard({
					image: webcamImage.imageAsBase64
				});
				break;
			}
			case ScanType.snapshot: {
				this.scannerService.getScanCard({
					image: webcamImage.imageAsBase64
				});
				break;
			}
			case ScanType.multiple: {
				this.searching = true;
				this.scannerService.getScanCards({
					image: webcamImage.imageAsBase64
				});
				break;
			}
		}
	}

	cameraWasSwitched(deviceId: string): void {
		console.log('active device: ' + deviceId);
		this.deviceId = deviceId;
	}

	get triggerObservable(): Observable<void> {
		return this.trigger.asObservable();
	}

	get nextWebcamObservable(): Observable<boolean | string> {
		return this.nextWebcam.asObservable();
	}

	// Start
	scannerStart() {
		this.timer = setInterval(() => {
			this.runScan();
		}, 250);
		this.scanning = true;
	}

	// Stop
	scannerStop() {
		clearInterval(this.timer);
		this.scanning = false;
	}

	// Start/stop scanner
	scannerToggle() {
		if (this.scanning) {
			this.scannerStop();
		}
		else {
			this.scannerStart();
		}
		this.runScan();
	}

	// Trigger a scan
	runScan() {
		if (!this.searching) {
			if (this.scannerMode != ScanType.scan) {
				this.loaderService.addItemLoading("scanning");
			}
			if (this.trigger) {
				this.trigger.next();
			}
			else {
				this.scannerStop();
				alert('NO TRIGGER');
			}
		}
	}
}

styleUrlsData.data:
@import "settings",
		"scaffold/typography";

.scanner-wrapper {
    margin: 0 auto;
    width: 400px;
    .scanner {
        border-radius: $border-radius;
        position: relative;
        padding-bottom: percentage(3.5/2.5);
		overflow: hidden;
		box-shadow: $shadow;
	}
	.scanner-modes {
		position: absolute;
		left: 0;
		right: 0;
		bottom: 0;
		z-index: 3;
	}
    .scanner-camera-wrapper {
        z-index: 1;
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        webcam {
			height: 100%;
			width: 100%;
			position: absolute;
			background: black;
            .webcam-wrapper {
				background: black;
				position: absolute;
				width: 100%;
				top: 40px;
				left: 0;
				right: 0;
				bottom: 40px;
				height: calc(100% - 80px); // Subtract header/footer menus to prevent video overlap
                video {
					display: block;
					width: auto;
					top: 50%;
					left: 50%;
					transform: translate(-50%, -50%);
					position: absolute;
					max-width: 100%;
					max-height: 100%;
                }
            }
        }
        .scanner-camera-overlay {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            transition: $transition;
            height: 80%;
            width: 80%;
            border: 1px solid $red;
            box-shadow: 0 0 4px 4px rgba($red, .4), inset 0 0 4px 4px rgba($red, .4);
            border-radius: 4%;
            &.scanning {
                border-color: $green;
                box-shadow: 0 0 4px 4px rgba($green, .4), inset 0 0 4px 4px rgba($green, .4);
            }
        }
    }
    .scanner-status {
        position: absolute;
        z-index: 50;
        top: 3.5 * 10px;
        right: 2.5 * 10px;
        width: 24px;
        height: 24px;
        background: $red;
        border-radius: 50%;
        box-shadow: 0 0 4px 4px rgba($red, .4);
        transition: $transition;
        &.scanning {
            background: $green;
            box-shadow: 0 0 4px 4px rgba($green, .4);
		}
		.scanner-fps {
			position: absolute;
			top: 50%;
			transform: translateY(-50%);
			right: 100%;
			margin-right: $spacing;
			background: rgba(white, .5);
			white-space: nowrap;
			border-radius: $border-radius;
			padding: 2px 8px;
			@extend .subheading;
		}
    }
    .scanner-options {
		position: absolute;
		left: 0;
		z-index: 5;
		> mb-menu {
			> .panel {
				border-top-left-radius: 0;
				border-bottom-left-radius: 0;
				border-top-right-radius: 0;
				> ul > li {
					> a {
						border-bottom-right-radius: $border-radius;
					}
				}
			}
		}
    }
    .scanner-action {
		position: absolute;
		overflow: hidden;
        z-index: 1;
        bottom: 40px;
        left: 50%;
        transform: translateX(-50%);
        width: 72px;
        height: 72px;
		border-top-left-radius: 50%;
		border-top-right-radius: 50%;
        transition: $transition;
        color: white;
        cursor: pointer;
        user-select: none;
		background: $green;
		box-shadow: 0 0 4px 4px rgba($green, .4);
		&:hover {
			box-shadow: 0 0 4px 8px rgba($green, .4);
		}
		fa-icon {
            position: absolute;
            top: 14px;
            left: 50%;
            transform: translateX(-50%);
            width: 24px;
            height: 24px;
		}
        &.scanning {
			background: $red;
			box-shadow: 0 0 4px 4px rgba($red, .4);
			&:hover {
				box-shadow: 0 0 4px 8px rgba($red, .4);
			}
        }
        span {
            position: absolute;
            bottom: 12px;
            left: 50%;
            transform: translateX(-50%);
            @extend .subheading;
        }
	}
	.scanner-result {
		position: absolute;
		top: 0;
		right: 0;
		z-index: 5;
		mb-menu {
			.panel {
				border-top-left-radius: 0;
				border-top-right-radius: 0;
				border-bottom-right-radius: 0;
				> ul > li > a {
					border-bottom-left-radius: $border-radius;
				}
			}
		}
	}
    .scanner-results-wrapper {
        position: absolute;
        bottom: 40px;
        left: 0;
        transition: $transition;
        z-index: 2;
        background: rgba(black, .5);
        border-radius: 4%;
		border-bottom-left-radius: 0;
		cursor: pointer;
        &.prompt {
            top: 0;
            left: 0;
            right: 0;
            bottom: 0;
        }
        .scanner-results {
            width: 100px;
            overflow: hidden;
            padding-bottom: percentage(3.5/2.5);
            border-radius: 4%;
            .scanner-result {
                position: absolute;
                top: 0;
                left: 0;
                right: 0;
                bottom: 0;
                padding-bottom: percentage(3.5/2.5);
                width: 100px;
                box-shadow: $shadow;
                border-radius: 4%;
				transition: $transition;
				transform-origin: bottom center;
				box-shadow: $shadow;
                .scanner-result-image {
                    display: block;
                    border-radius: 4%;
                }
                @for $i from 1 through 20 {
                    &:nth-child(#{$i}) {
                        margin-top: 14px * ($i - 1);
                        //margin-left: 4px * ($i - 1);
                    }
                }
            }
        }
        .scanner-results-count {
            z-index: 10;
            background: $green;
            color: white;
            border-radius: 999px;
            position: absolute;
            top: -8px;
            right: -8px;
            box-shadow: $shadow;
            width: 32px;
            height: 32px;
            span {
                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
            }
        }
    }
    .scanner-errors {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        background: white;
        border-radius: $border-radius;
        box-shadow: $shadow;
        z-index: 4;
        padding: 32px;
        width: 90%;
        overflow: auto;
        max-width: 90%;
        max-height: 90%;
    }
    .scanner-errors-overlay {
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: rgba(black, .8);
        z-index: 3;
    }
}

