@import "settings";

.tag {
	display: flex;
	padding: 4px 6px;
	text-transform: uppercase;
	font-size: 12px;
	line-height: 1;
	background: $panel-bg;
	border-radius: $border-radius;
	align-items: center;
	box-shadow: $shadow-sheen;
	position: relative;
	&.primary {
		background: $primary-color;
		color: white;
	}
	&.transparent {
		background: transparent;
		color: inherit;
	}

	&.card-rarity {
		transition: $transition;
		cursor: pointer;
		&:hover {
			box-shadow: $shadow-sheen, $shadow-focus;
		}
	}

	fa-icon {
		+ span {
			padding-left: $spacing / 2;
		}
	}

	// Sheen
	&:after {
		content: "";
		border-radius: $border-radius;
		width: 100%;
		height: 100%;
		box-shadow: $shadow-sheen;
		z-index: 10;
		position: absolute;
		border: $border-strong;
		top: 0;
		left: 0;
	}

	$promo: black;
	$common: grey;
	$uncommon: darken(white, 10%);
	$rare-non-holo: #119100;
	$rare-holo: #004180;
	$rare-ultra: #5d1e89;
	$rare-secret: #bb6005;

	$rarity: (
		"promo": $promo,
		"common": $common,
		"uncommon": $uncommon,
		"rare": $rare-non-holo,
		"rare-holo": $rare-holo,
		"rare-ultra": $rare-ultra,
		"rare-secret": $rare-secret,
		"rare-prime": $rare-ultra,
	);

	@each $rarity-name, $rarity-color in $rarity {
		&.#{$rarity-name} {
			background: $rarity-color;
			color: lighten($rarity-color, 50%);
			//box-shadow: 0 0 8px 4px rgba(lighten($rarity-color, 10%), .5);
			border-color: darken($rarity-color, 10%);
			&:before {
				background-color: lighten($rarity-color, 50%);
			}
		}
	}
	&.common {
		&:before {
			content: "";
			border-radius: 50%;
			width: 10px;
			height: 10px;
			display: inline-block;
			margin-right: 4px;
		}
	}
	&.uncommon {
		color: rgba(black, 0.5);
		//box-shadow: 0 0 8px 4px rgba($font-color, .1);
		&:before {
			content: "";
			display: inline-block;
			width: 8px;
			height: 8px;
			transform: rotate(45deg);
			background: rgba(black, 0.5);
		}
		span {
			margin-left: 6px;
		}
	}
	&.promo,
	&.rare,
	&.rare-ultra,
	&.rare-holo {
		&:before {
			content: "";
			display: inline-block;
			margin-right: 4px;
			width: 12px;
			height: 12px;
			clip-path: polygon(
				50% 0%,
				61% 35%,
				98% 35%,
				68% 57%,
				79% 91%,
				50% 70%,
				21% 91%,
				32% 57%,
				2% 35%,
				39% 35%
			);
		}
	}
	&.promo {
		color: white;
		opacity: 0.8;
		&:before {
			background: white;
		}
	}
	&.rare-secret {
		&:before {
			content: "";
			display: inline-block;
			margin-right: 4px;
			width: 12px;
			height: 12px;
			clip-path: polygon(
				50% 0%,
				61% 35%,
				98% 35%,
