@import "settings";

.toggle-wrapper {
	display: flex;
}

.toggle {
	display: block;
	position: relative;
	user-select: none;
	cursor: pointer;
	input[type="checkbox"] {
		margin: 0;
		appearance: none;
		width: 100%;
		height: 40px;
		border-radius: $border-radius;
		background: $input-bg;
		border: $border;
		vertical-align: middle;
		position: absolute;
		&:before {
			content: "";
			position: absolute;
			width: 34px;
			top: 2px;
			left: 2px;
			background: rgba($font-color, 0.5);
			bottom: 2px;
			transition: $transition;
			border-radius: $border-radius;
		}
		&:checked {
			&:before {
				left: calc(100% - 34px - 2px);
				background: $primary-color;
			}
			~ .textChecked {
				opacity: 1;
			}
			~ .text {
				opacity: 0;
			}
		}
		&:hover {
			border-color: $primary-color;
		}
		&:focus {
			border-color: $primary-color;
			box-shadow: $shadow-focus, $shadow;
		}
	}
	.text {
		z-index: 1;
		position: relative;
		padding: 10px 48px;
		display: block;
		text-align: center;
		transition: $transition;
		padding-right: 16px;
	}
	.textChecked {
		z-index: 1;
		position: relative;
		padding: 10px 48px;
		display: block;
		text-align: center;
		transition: $transition;
		position: relative;
		margin-top: -40px;
		opacity: 0;
		padding-left: 16px;
	}
}

